              }
            }
          },
          {
            props: { variant: 'tonal', color: 'secondary', orientation: 'vertical' },
            style: {
              '& .MuiButtonGroup-firstButton, & .MuiButtonGroup-middleButton': {
                '&, &.Mui-disabled': {
