              }
            }
          },
          {
            props: { variant: 'tonal', color: 'warning', orientation: 'vertical' },
            style: {
              '& .MuiButtonGroup-firstButton, & .MuiButtonGroup-middleButton': {
                '&, &.Mui-disabled': {
                  borderBottom: '1px solid var(--mui-palette-warning-darkOpacity)'
                }
