export default function DashboardFaqPage() {
  const [searchQuery, setSearchQuery] = useState('')
  const [expanded, setExpanded] = useState<string | false>(false)

  const handleChange = (panel: string) => (_: React.SyntheticEvent, isExpanded: boolean) => {
    setExpanded(isExpanded ? panel : false)
  }

  const filteredCategories = faqCategories.map(category => ({
    ...category,
    questions: category.questions.filter(
      q => q.question.toLowerCase().includes(searchQuery.toLowerCase()) ||
           q.answer.toLowerCase().includes(searchQuery.toLowerCase())
    )
  })).filter(category => category.questions.length > 0)

  return (
    <Box className="p-6">
      <Box className="text-center mb-8">
        <Typography variant="h4" sx={{ color: '#5D596C', fontWeight: 600, mb: 1 }}>
          Foire Aux Questions
        </Typography>
        <Typography color="text.secondary" className="mb-4">
          Trouvez rapidement les réponses à vos questions
        </Typography>

        {/* Search */}
        <Box className="max-w-md mx-auto">
          <TextField
            fullWidth
            placeholder="Rechercher une question..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <i className="tabler-search" style={{ color: '#7367F0' }} />
                </InputAdornment>
              )
            }}
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
                '&:hover fieldset': { borderColor: '#7367F0' },
                '&.Mui-focused fieldset': { borderColor: '#7367F0' }
              }
            }}
          />
        </Box>
      </Box>

      <Grid container spacing={4}>
        {filteredCategories.map((category) => (
          <Grid item xs={12} md={6} key={category.title}>
            <Card sx={{ borderRadius: 2, boxShadow: '0 2px 10px rgba(0,0,0,0.08)', height: '100%' }}>
              <CardContent className="p-6">
                <Box className="flex items-center gap-3 mb-4">
                  <Box 
                    className="w-10 h-10 rounded-lg flex items-center justify-center"
                    sx={{ bgcolor: `${category.color}20` }}
                  >
                    <i className={`${category.icon} text-xl`} style={{ color: category.color }} />
                  </Box>
                  <Typography variant="h6" sx={{ color: '#5D596C', fontWeight: 600 }}>
                    {category.title}
                  </Typography>
                </Box>

                {category.questions.map((faq, index) => (
                  <Accordion 
                    key={index}
                    expanded={expanded === `${category.title}-${index}`}
                    onChange={handleChange(`${category.title}-${index}`)}
                    sx={{ 
                      boxShadow: 'none',
                      '&:before': { display: 'none' },
                      borderBottom: index < category.questions.length - 1 ? '1px solid #eee' : 'none'
                    }}
                  >
                    <AccordionSummary
                      expandIcon={<i className="tabler-chevron-down" />}
                      sx={{ px: 0 }}
                    >
                      <Typography sx={{ fontWeight: 500, color: '#5D596C' }}>
                        {faq.question}
                      </Typography>
                    </AccordionSummary>
                    <AccordionDetails sx={{ px: 0, pt: 0 }}>
                      <Typography 
                        color="text.secondary" 
                        sx={{ 
                          lineHeight: 1.7,
                          wordBreak: 'break-word'
                        }}
                      >
                        {faq.answer}
                      </Typography>
                    </AccordionDetails>
                  </Accordion>
                ))}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Contact Support */}
      <Card sx={{ mt: 6, borderRadius: 2, boxShadow: '0 2px 10px rgba(0,0,0,0.08)' }}>
        <CardContent className="p-6">
          <Box className="flex items-center justify-between flex-wrap gap-4">
            <Box>
              <Typography variant="h6" sx={{ fontWeight: 600, color: '#5D596C' }}>
                Vous ne trouvez pas la réponse ?
              </Typography>
              <Typography color="text.secondary">
                Notre équipe est disponible pour vous aider
              </Typography>
            </Box>
            <Button 
              variant="contained"
              startIcon={<i className="tabler-mail" />}
              sx={{ bgcolor: '#7367F0', '&:hover': { bgcolor: '#685DD8' } }}
            >
              Contacter le support
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Box>
  )
}

'use client'

import { useState, useEffect, useRef } from 'react'
import { useRouter } from 'next/navigation'
import { createClient } from '@/utils/supabase'
import Box from '@mui/material/Box'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import Typography from '@mui/material/Typography'
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import Avatar from '@mui/material/Avatar'
import Grid from '@mui/material/Grid'
import Divider from '@mui/material/Divider'
import Alert from '@mui/material/Alert'
import IconButton from '@mui/material/IconButton'
import CircularProgress from '@mui/material/CircularProgress'

export default function ProfilePage() {
  const supabase = createClient()
  const router = useRouter()
  const fileInputRef = useRef<HTMLInputElement>(null)
  const [user, setUser] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [uploadingAvatar, setUploadingAvatar] = useState(false)
  const [avatarUrl, setAvatarUrl] = useState<string | null>(null)
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null)

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    company: '',
    phone: ''
  })

  useEffect(() => {
    const getUser = async () => {
      const { data: { user } } = await supabase.auth.getUser()
      if (user) {
        setUser(user)
        setAvatarUrl(user.user_metadata?.avatar_url || null)
        setFormData({
          fullName: user.user_metadata?.full_name || '',
          email: user.email || '',
          company: user.user_metadata?.company || '',
          phone: user.user_metadata?.phone || ''
        })
      }
      setLoading(false)
    }
    getUser()
  }, [])

  const handleAvatarClick = () => {
    fileInputRef.current?.click()
  }

  const handleAvatarChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file || !user) return

    setUploadingAvatar(true)
    setMessage(null)

    try {
      // Create unique filename
      const fileExt = file.name.split('.').pop()
      const fileName = `${user.id}-${Date.now()}.${fileExt}`
      const filePath = `avatars/${fileName}`

      // Upload to Supabase Storage
      const { error: uploadError } = await supabase.storage
        .from('avatars')
        .upload(filePath, file, { upsert: true })

      if (uploadError) {
        // If bucket doesn't exist, show helpful message
        if (uploadError.message.includes('Bucket not found')) {
          setMessage({ 
            type: 'error', 
            text: 'Le bucket "avatars" n\'existe pas. Créez-le dans Supabase Storage.' 
          })
        } else {
          setMessage({ type: 'error', text: uploadError.message })
        }
        setUploadingAvatar(false)
        return
      }

      // Get public URL
      const { data: { publicUrl } } = supabase.storage
        .from('avatars')
        .getPublicUrl(filePath)

      // Update user metadata with new avatar URL
      const { error: updateError } = await supabase.auth.updateUser({
        data: { avatar_url: publicUrl }
      })

      if (updateError) {
        setMessage({ type: 'error', text: updateError.message })
      } else {
        setAvatarUrl(publicUrl)
        setMessage({ type: 'success', text: 'Photo de profil mise à jour !' })
        // Broadcast update to other components
        window.dispatchEvent(new Event('user:updated'))
      }
    } catch (error: any) {
      setMessage({ type: 'error', text: error.message || 'Erreur lors de l\'upload' })
    }

    setUploadingAvatar(false)
  }

  const handleSave = async () => {
    setSaving(true)
    setMessage(null)

    const { error } = await supabase.auth.updateUser({
      data: {
        full_name: formData.fullName,
        company: formData.company,
        phone: formData.phone
      }
    })

    if (error) {
      setMessage({ type: 'error', text: error.message })
    } else {
      setMessage({ type: 'success', text: 'Profil mis à jour avec succès !' })
    }
    setSaving(false)
  }

  if (loading) {
    return (
      <Box className="flex items-center justify-center min-h-[400px]">
        <Typography>Chargement...</Typography>
      </Box>
    )
  }

  return (
    <Box className="p-6">
      <Typography variant="h4" className="mb-6" sx={{ color: '#5D596C', fontWeight: 600 }}>
        Mon Profil
      </Typography>

      {message && (
        <Alert severity={message.type} className="mb-4" onClose={() => setMessage(null)}>
          {message.text}
        </Alert>
      )}

      <Grid container spacing={4}>
        {/* Profile Card */}
        <Grid item xs={12} md={4}>
          <Card sx={{ borderRadius: 2, boxShadow: '0 2px 10px rgba(0,0,0,0.08)' }}>
            <CardContent className="flex flex-col items-center py-8">
              {/* Avatar with upload */}
              <Box sx={{ position: 'relative', mb: 3 }}>
                <Avatar
                  src={avatarUrl || undefined}
                  sx={{ 
                    width: 120, 
                    height: 120,
                    bgcolor: '#7367F0',
                    fontSize: '2.5rem',
                    cursor: 'pointer',
                    transition: 'opacity 0.2s',
                    '&:hover': { opacity: 0.8 }
                  }}
                  onClick={handleAvatarClick}
                >
                  {formData.fullName?.charAt(0).toUpperCase() || formData.email?.charAt(0).toUpperCase()}
                </Avatar>

                {/* Upload overlay */}
                <IconButton
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    right: 0,
                    bgcolor: '#7367F0',
                    color: 'white',
                    width: 36,
                    height: 36,
                    '&:hover': { bgcolor: '#685DD8' }
                  }}
                  onClick={handleAvatarClick}
                  disabled={uploadingAvatar}
                >
                  {uploadingAvatar ? (
                    <CircularProgress size={20} color="inherit" />
                  ) : (
                    <i className="tabler-camera text-lg" />
                  )}
                </IconButton>

                {/* Hidden file input */}
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleAvatarChange}
                />
              </Box>

              <Typography variant="body2" color="text.secondary" className="mb-2 text-center">
                Cliquez pour changer la photo
              </Typography>

