// Styled Custom Components
const LoginIllustration = styled('img')(({ theme }) => ({
  zIndex: 2,
  blockSize: 'auto',
  maxBlockSize: 680,
  maxInlineSize: '100%',
  margin: theme.spacing(12),
  [theme.breakpoints.down(1536)]: {
    maxBlockSize: 550
  },
  [theme.breakpoints.down('lg')]: {
    maxBlockSize: 450
  }
}))

const MaskImg = styled('img')({
  blockSize: 'auto',
  maxBlockSize: 355,
  inlineSize: '100%',
  position: 'absolute',
  insetBlockEnd: 0,
  zIndex: -1
})

const Register = ({ mode }: { mode: SystemMode }) => {
  // States
  const [isPasswordShown, setIsPasswordShown] = useState(false)
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [username, setUsername] = useState('')
  const [error, setError] = useState<string | null>(null)
  const [success, setSuccess] = useState<string | null>(null)

  const supabase = createClient()

  // Vars
  const darkImg = '/images/pages/auth-mask-dark.png'
  const lightImg = '/images/pages/auth-mask-light.png'
  const darkIllustration = '/images/illustrations/auth/v2-register-dark.png'
  const lightIllustration = '/images/illustrations/auth/v2-register-light.png'
  const borderedDarkIllustration = '/images/illustrations/auth/v2-register-dark-border.png'
  const borderedLightIllustration = '/images/illustrations/auth/v2-register-light-border.png'

  // Hooks
  const router = useRouter()
  const { settings } = useSettings()
  const theme = useTheme()
  const hidden = useMediaQuery(theme.breakpoints.down('md'))
  const authBackground = useImageVariant(mode, lightImg, darkImg)

  const characterIllustration = useImageVariant(
    mode,
    lightIllustration,
    darkIllustration,
    borderedLightIllustration,
    borderedDarkIllustration
  )

  const handleClickShowPassword = () => setIsPasswordShown(show => !show)

  return (
    <div className='flex bs-full justify-center'>
      <div
        className={classnames(
          'flex bs-full items-center justify-center flex-1 min-bs-[100dvh] relative p-6 max-md:hidden',
          {
            'border-ie': settings.skin === 'bordered'
          }
        )}
      >
        <LoginIllustration src={characterIllustration} alt='character-illustration' />
        {!hidden && (
          <MaskImg
            alt='mask'
            src={authBackground}
            className={classnames({ 'scale-x-[-1]': theme.direction === 'rtl' })}
          />
        )}
      </div>
      <div className='flex justify-center items-center bs-full bg-backgroundPaper !min-is-full p-6 md:!min-is-[unset] md:p-12 md:is-[480px]'>
        <Link className='absolute block-start-5 sm:block-start-[33px] inline-start-6 sm:inline-start-[38px]'>
          <Logo />
        </Link>
        <div className='flex flex-col gap-6 is-full sm:is-auto md:is-full sm:max-is-[400px] md:max-is-[unset] mbs-11 sm:mbs-14 md:mbs-0'>
          <div className='flex flex-col gap-1'>
            <Typography variant='h4'>Créez votre compte 🚀</Typography>
            <Typography>Commencez votre veille juridique automatisée</Typography>
          </div>
          <form
            noValidate
            autoComplete='off'
            onSubmit={async e => {
              e.preventDefault()
              setError(null)
              setSuccess(null)
              const { data, error } = await supabase.auth.signUp({
                email,
                password,
                options: {
                  data: {
                    full_name: username
                  }
                }
              })
              if (error) {
                setError(error.message)
              } else {
                setSuccess('Inscription réussie ! Vérifiez votre email pour confirmer.')
                // Optionally redirect or wait
                // router.push('/login')
              }
            }}
            className='flex flex-col gap-5'
          >
            {error && (
              <Typography color='error' className='text-center'>
                {error}
              </Typography>
            )}
            {success && (
              <Typography color='primary' className='text-center'>
                {success}
              </Typography>
            )}
            <CustomTextField
              autoFocus
              fullWidth
              label='Nom complet'
              placeholder='Jean Dupont'
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
            <CustomTextField
              fullWidth
              label='Email'
              placeholder='Votre adresse email'
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <CustomTextField
              fullWidth
              label='Mot de passe'
              placeholder='············'
              id='outlined-adornment-password'
              type={isPasswordShown ? 'text' : 'password'}
              value={password}
              onChange={e => setPassword(e.target.value)}
              slotProps={{
                input: {
                  endAdornment: (
                    <InputAdornment position='end'>
                      <IconButton edge='end' onClick={handleClickShowPassword} onMouseDown={e => e.preventDefault()}>
                        <i className={isPasswordShown ? 'tabler-eye-off' : 'tabler-eye'} />
                      </IconButton>
                    </InputAdornment>
                  )
                }
              }}
            />
            <FormControlLabel
              control={<Checkbox />}
              label={
                <>
                  <span>J&#39;accepte les </span>
                  <Link href='/' onClick={e => e.preventDefault()} className='text-primary'>
                    CGU et politique de confidentialité
                  </Link>
                </>
              }
            />
            <Button fullWidth variant='contained' type='submit' sx={{ backgroundColor: '#7367F0' }}>
              S&#39;inscrire
            </Button>
            <div className='flex justify-center items-center flex-wrap gap-2'>
              <Typography>Déjà un compte ?</Typography>
              <Typography component={Link} href='/login' color='primary.main'>
                Se connecter
              </Typography>
            </div>
            <Divider className='gap-2 text-textPrimary'>ou</Divider>
            <div className='flex justify-center items-center gap-1.5'>
              <IconButton className='text-facebook' size='small'>
                <i className='tabler-brand-facebook-filled' />
              </IconButton>
              <IconButton className='text-twitter' size='small'>
                <i className='tabler-brand-twitter-filled' />
              </IconButton>
              <IconButton className='text-textPrimary' size='small'>
                <i className='tabler-brand-github-filled' />
              </IconButton>
              <IconButton className='text-error' size='small'>
                <i className='tabler-brand-google-filled' />
              </IconButton>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

export default Register

'use client'

// React Imports
import { useState, useEffect } from 'react'
import type { ChangeEvent } from 'react'

// MUI Imports
import Grid from '@mui/material/Grid2'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import MenuItem from '@mui/material/MenuItem'
import Alert from '@mui/material/Alert'
import CircularProgress from '@mui/material/CircularProgress'
import Divider from '@mui/material/Divider'

// Component Imports
import CustomTextField from '@core/components/mui/TextField'

// Supabase Imports
import { createClient } from '@/utils/supabase'
import type { User } from '@supabase/supabase-js'

// Context Imports
import { useLanguage } from '@/contexts/LanguageContext'

type Data = {
  firstName: string
  lastName: string
  email: string
  organization: string
  phoneNumber: string
  address: string
  state: string
  zipCode: string
  country: string
  language: string // Changed to single string
  timezone: string
  currency: string
}

// Vars
const initialData: Data = {
  firstName: '',
  lastName: '',
  email: '',
  organization: '',
  phoneNumber: '',
  address: '',
  state: '',
  zipCode: '',
  country: '',
  language: 'Français', // Default single value
  timezone: '',
  currency: 'eur'
}

// Only French and English as requested
const languageData = ['Français', 'Anglais']

const AccountDetails = () => {
  // States
  const [formData, setFormData] = useState<Data>(initialData)
  const [fileInput, setFileInput] = useState<string>('')
  const [imgSrc, setImgSrc] = useState<string>('')
  const [user, setUser] = useState<User | null>(null)
  const [loading, setLoading] = useState(true)
  const [uploading, setUploading] = useState(false)
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null)

  const supabase = createClient()
  const { t } = useLanguage()

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true)
      const {
        data: { user }
      } = await supabase.auth.getUser()
      if (user) {
        setUser(user)

        // Setup initial avatar
        setImgSrc(user.user_metadata?.avatar_url || '/images/avatars/1.png')

        // Fetch profile data
        const { data: profile } = await supabase.from('profiles').select('*').eq('id', user.id).single()

        let firstName = ''
        let lastName = ''

        // Initialize from user metadata if available
        if (user.user_metadata?.full_name) {
          const names = user.user_metadata.full_name.split(' ')
          firstName = names[0] || ''
          lastName = names.slice(1).join(' ') || ''
        }

        const newFormData: Data = {
          firstName,
          lastName,
          email: user.email || '',
          organization: user.user_metadata?.company || '',
          phoneNumber: user.user_metadata?.phone || '',
          address: '',
          state: '',
          zipCode: '',
          country: '',
          language: 'Français',
          timezone: '',
          currency: 'eur'
        }

        if (profile) {
          if (profile.full_name) {
            const names = profile.full_name.split(' ')
            firstName = names[0] || ''
            lastName = names.slice(1).join(' ') || ''
          }

          newFormData.firstName = firstName
          newFormData.lastName = lastName
          newFormData.organization = profile.organization || newFormData.organization
          newFormData.phoneNumber = profile.phone || newFormData.phoneNumber
          newFormData.address = profile.address || ''
          newFormData.state = profile.state || ''
          newFormData.zipCode = profile.zip_code || ''
          newFormData.country = profile.country || ''
          // Handle existing language or default
          newFormData.language = profile.language || 'Français'
          newFormData.timezone = profile.timezone || ''
          newFormData.currency = profile.currency || 'eur'
        }

        setFormData(newFormData)
      }
      setLoading(false)
    }

    fetchData()
  }, [])

  const handleFormChange = (field: keyof Data, value: Data[keyof Data]) => {
    setFormData({ ...formData, [field]: value })
  }

  const handleFileInputChange = async (file: ChangeEvent) => {
    const reader = new FileReader()
    const { files } = file.target as HTMLInputElement

    if (files && files.length !== 0 && user) {
      const selectedFile = files[0]
      reader.onload = () => setImgSrc(reader.result as string)
      reader.readAsDataURL(selectedFile)

      // Upload to Supabase
      setUploading(true)
      try {
        const fileExt = selectedFile.name.split('.').pop()
        const fileName = `${user.id}-${Date.now()}.${fileExt}`
        const filePath = `avatars/${fileName}`

        const { error: uploadError } = await supabase.storage
          .from('avatars')
          .upload(filePath, selectedFile, { upsert: true })

        if (uploadError) throw uploadError

        const {
          data: { publicUrl }
        } = supabase.storage.from('avatars').getPublicUrl(filePath)

        // Update auth metadata
        const { error: updateError } = await supabase.auth.updateUser({
          data: { avatar_url: publicUrl }
        })

        if (updateError) throw updateError

        // Also update profiles table
        await supabase.from('profiles').update({ avatar_url: publicUrl }).eq('id', user.id)

        // Dispatch event for header sync
        window.dispatchEvent(new Event('user:updated'))

        setMessage({ type: 'success', text: t.account.upload_success })
      } catch (error: any) {
        console.error(error)
        setMessage({ type: 'error', text: t.account.upload_error })
      } finally {
        setUploading(false)
      }
    }
  }

  const handleFileInputReset = async () => {
    setFileInput('')
    setImgSrc('/images/avatars/1.png')
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!user) return

    setMessage(null)
    setLoading(true)

    try {
      const fullName = `${formData.firstName} ${formData.lastName}`.trim()

      // 1. Update Profile Table
      const { error: profileError } = await supabase.from('profiles').upsert({
        id: user.id,
        full_name: fullName,
        organization: formData.organization,
        phone: formData.phoneNumber,
        address: formData.address,
        state: formData.state,
        zip_code: formData.zipCode,
        country: formData.country,
        language: formData.language, // No longer an array
        timezone: formData.timezone,
        currency: formData.currency,
        updated_at: new Date().toISOString()
      })

      if (profileError) throw profileError

      // 2. Update Auth Metadata
      const { error: authError } = await supabase.auth.updateUser({
        data: {
          full_name: fullName,
          company: formData.organization,
          phone: formData.phoneNumber
        }
      })

      if (authError) throw authError

      // Dispatch event for header sync
      window.dispatchEvent(new Event('user:updated'))

      setMessage({ type: 'success', text: t.account.success_update })
    } catch (error: any) {
      setMessage({ type: 'error', text: t.account.error_update })
    } finally {
      setLoading(false)
    }
  }

  if (loading && !user) {
    return (
      <div className='p-4 flex justify-center'>
        <CircularProgress />
      </div>
    )
  }

  return (
    <Card>
      <CardContent className='mbe-4'>
        <div className='flex max-sm:flex-col items-center gap-6'>
          <img height={100} width={100} className='rounded' src={imgSrc} alt='Profile' />
          <div className='flex flex-grow flex-col gap-4'>
            <div className='flex flex-col sm:flex-row gap-4'>
              <Button
                component='label'
                variant='contained'
                htmlFor='account-settings-upload-image'
                disabled={uploading}
              >
                {uploading ? t.common.loading : t.account.change_photo}
                <input
                  hidden
                  type='file'
                  value={fileInput}
                  accept='image/png, image/jpeg'
                  onChange={handleFileInputChange}
                  id='account-settings-upload-image'
                />
              </Button>
              <Button variant='tonal' color='secondary' onClick={handleFileInputReset}>
                {t.account.reset_photo}
              </Button>
            </div>
            <Typography>{t.account.upload_text}</Typography>
          </div>
        </div>
      </CardContent>
      <Divider />
      <CardContent>
        <form onSubmit={handleSubmit}>
          {message && (
            <Alert severity={message.type} sx={{ mb: 4 }}>
              {message.text}
            </Alert>
          )}
          <Grid container spacing={6}>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.first_name}
                value={formData.firstName}
                placeholder='Jean'
                onChange={e => handleFormChange('firstName', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.last_name}
                value={formData.lastName}
                placeholder='Dupont'
                onChange={e => handleFormChange('lastName', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.email}
                value={formData.email}
                placeholder='jean.dupont@email.com'
                disabled
                helperText={t.account.email_helper}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.organization}
                value={formData.organization}
                placeholder='Ma Société'
                onChange={e => handleFormChange('organization', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.phone}
                value={formData.phoneNumber}
                placeholder='+33 6 12 34 56 78'
                onChange={e => handleFormChange('phoneNumber', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.address}
                value={formData.address}
                placeholder='123 Rue de la Paix'
                onChange={e => handleFormChange('address', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                label={t.account.ville}
                value={formData.state}
                placeholder='Paris'
                onChange={e => handleFormChange('state', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                fullWidth
                type='text'
                label={t.account.zip}
                value={formData.zipCode}
                placeholder='75000'
                onChange={e => handleFormChange('zipCode', e.target.value)}
              />
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                select
                fullWidth
                label={t.account.country}
                value={formData.country}
                onChange={e => handleFormChange('country', e.target.value)}
              >
                <MenuItem value='france'>France</MenuItem>
                <MenuItem value='usa'>États-Unis</MenuItem>
                <MenuItem value='uk'>Royaume-Uni</MenuItem>
                <MenuItem value='germany'>Allemagne</MenuItem>
                <MenuItem value='australia'>Australie</MenuItem>
              </CustomTextField>
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              {/* Single Select Language */}
              <CustomTextField
                select
                fullWidth
                label={t.account.language}
                value={formData.language}
                onChange={e => handleFormChange('language', e.target.value)}
              >
                {languageData.map(name => (
                  <MenuItem key={name} value={name}>
                    {name}
                  </MenuItem>
                ))}
              </CustomTextField>
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                select
                fullWidth
                label={t.account.timezone}
                value={formData.timezone}
                onChange={e => handleFormChange('timezone', e.target.value)}
                slotProps={{
                  select: { MenuProps: { PaperProps: { style: { maxHeight: 250 } } } }
                }}
              >
                <MenuItem value='gmt-12'>(GMT-12:00) International Date Line West</MenuItem>
                <MenuItem value='gmt-01'>(GMT+01:00) Paris, Bruxelles, Copenhague, Madrid</MenuItem>
                <MenuItem value='gmt-00'>(GMT+00:00) Londres, Dublin, Édimbourg</MenuItem>
                <MenuItem value='gmt-05'>(GMT-05:00) Heure de l'Est (US & Canada)</MenuItem>
                <MenuItem value='gmt-08'>(GMT-08:00) Heure du Pacifique (US & Canada)</MenuItem>
              </CustomTextField>
            </Grid>
            <Grid size={{ xs: 12, sm: 6 }}>
              <CustomTextField
                select
                fullWidth
                label={t.account.currency}
                value={formData.currency}
                onChange={e => handleFormChange('currency', e.target.value)}
              >
                <MenuItem value='eur'>EUR (€)</MenuItem>
                <MenuItem value='usd'>USD ($)</MenuItem>
                <MenuItem value='gbp'>GBP (£)</MenuItem>
              </CustomTextField>
            </Grid>
            <Grid size={{ xs: 12 }} className='flex gap-4 flex-wrap'>
              <Button variant='contained' type='submit' disabled={loading}>
                {loading ? t.common.saving : t.common.save}
              </Button>
              <Button variant='tonal' type='reset' color='secondary' onClick={() => setFormData(initialData)}>
                {t.common.reset}
              </Button>
            </Grid>
          </Grid>
        </form>
      </CardContent>
    </Card>
  )
}

export default AccountDetails

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Typography from '@mui/material/Typography'
import Switch from '@mui/material/Switch'
import Button from '@mui/material/Button'
import Box from '@mui/material/Box'
import CircularProgress from '@mui/material/CircularProgress'
import Alert from '@mui/material/Alert'

// Supabase Imports
import { createClient } from '@/utils/supabase'

// Context Imports
import { useLanguage } from '@/contexts/LanguageContext'

type ConnectedAccount = {
  id: string
  name: string
  subtitleKey: string
  logo: string
}

const connectedAccountsList: ConnectedAccount[] = [
  { id: 'google', name: 'Google', subtitleKey: 'google_sub', logo: 'tabler-brand-google' },
  { id: 'slack', name: 'Slack', subtitleKey: 'slack_sub', logo: 'tabler-brand-slack' },
  { id: 'github', name: 'Github', subtitleKey: 'github_sub', logo: 'tabler-brand-github' },
  { id: 'mailchimp', name: 'Mailchimp', subtitleKey: 'mailchimp_sub', logo: 'tabler-brand-mailchimp' },
  { id: 'asana', name: 'Asana', subtitleKey: 'asana_sub', logo: 'tabler-brand-asana' }
]

const socialAccountsList: ConnectedAccount[] = [
  { id: 'facebook', name: 'Facebook', subtitleKey: 'not_connected', logo: 'tabler-brand-facebook' },
  { id: 'twitter', name: 'Twitter', subtitleKey: 'not_connected', logo: 'tabler-brand-twitter' }, // Simplification for translations
  { id: 'instagram', name: 'Instagram', subtitleKey: 'not_connected', logo: 'tabler-brand-instagram' },
  { id: 'dribbble', name: 'Dribbble', subtitleKey: 'not_connected', logo: 'tabler-brand-dribbble' },
  { id: 'behance', name: 'Behance', subtitleKey: 'not_connected', logo: 'tabler-brand-behance' }
]

const ConnectionsTab = () => {
  const [connections, setConnections] = useState<Record<string, boolean>>({})
  const [loading, setLoading] = useState(true)
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null)

  const supabase = createClient()
  const { t } = useLanguage()

  useEffect(() => {
    const fetchConnections = async () => {
      setLoading(true)
      const {
        data: { user }
      } = await supabase.auth.getUser()
      if (user) {
        const { data } = await supabase.from('user_settings').select('connections').eq('user_id', user.id).single()

        if (data?.connections) {
          setConnections(data.connections)
        }
      }
      setLoading(false)
    }

    fetchConnections()
  }, [])

  const handleToggle = async (id: string, checked: boolean) => {
    // Optimistic update
    const newConnections = { ...connections, [id]: checked }
    setConnections(newConnections)

    const {
      data: { user }
    } = await supabase.auth.getUser()
    if (user) {
      const { error } = await supabase.from('user_settings').upsert({
        user_id: user.id,
        connections: newConnections,
        updated_at: new Date().toISOString()
      })

      if (error) {
        setMessage({ type: 'error', text: t.common.error })
        // Revert
        setConnections(connections)
      }
    }
  }

  const handleSocialConnect = async (id: string) => {
    // Mock connecting logic
    const isConnected = !!connections[id]
    handleToggle(id, !isConnected)
  }

  if (loading)
    return (
      <div className='p-4 flex justify-center'>
        <CircularProgress />
      </div>
    )

  return (
    <Grid container spacing={6}>
      <Grid size={{ xs: 12 }}>
        {message && (
          <Alert severity={message.type} onClose={() => setMessage(null)}>
            {message.text}
          </Alert>
        )}
      </Grid>

      {/* Connected Accounts */}
      <Grid size={{ xs: 12, md: 6 }}>
        <Card>
          <CardHeader title={t.connections.connected_title} subheader={t.connections.connected_sub} />
          <CardContent className='flex flex-col gap-4'>
            {connectedAccountsList.map(account => (
              <div key={account.id} className='flex items-center justify-between'>
                <div className='flex items-center gap-3'>
                  <Box className='flex items-center justify-center w-9 h-9 rounded bg-actionHover'>
                    <i className={`${account.logo} text-xl`} />
                  </Box>
                  <div>
                    <Typography className='font-medium'>{account.name}</Typography>
                    <Typography variant='caption'>
                      {t.connections[account.subtitleKey as keyof typeof t.connections]}
                    </Typography>
                  </div>
                </div>
                <Switch
                  checked={!!connections[account.id]}
                  onChange={(e, checked) => handleToggle(account.id, checked)}
                />
              </div>
            ))}
          </CardContent>
        </Card>
      </Grid>

      {/* Social Accounts */}
      <Grid size={{ xs: 12, md: 6 }}>
        <Card>
          <CardHeader title={t.connections.social_title} subheader={t.connections.social_sub} />
          <CardContent className='flex flex-col gap-4'>
            {socialAccountsList.map(account => {
              const isConnected = !!connections[account.id]
              return (
                <div key={account.id} className='flex items-center justify-between'>
                  <div className='flex items-center gap-3'>
                    <Box className='flex items-center justify-center w-9 h-9 rounded bg-actionHover'>
                      <i className={`${account.logo} text-xl`} />
                    </Box>
                    <div>
                      <Typography className='font-medium'>{account.name}</Typography>
                      <Typography
                        variant='caption'
                        color={isConnected ? 'primary' : 'textSecondary'}
                        component={isConnected ? 'a' : 'span'}
                        href='#'
                      >
                        {isConnected
                          ? account.id === 'twitter' || account.id === 'instagram'
                            ? '@Pixinvent'
                            : t.connections.not_connected
                          : t.connections.not_connected}
                      </Typography>
                    </div>
                  </div>
                  <Button
                    variant='tonal'
                    size='small'
                    color={isConnected ? 'error' : 'secondary'}
                    className='min-w-[40px] px-2'
                    onClick={() => handleSocialConnect(account.id)}
                  >
                    <i className={isConnected ? 'tabler-trash' : 'tabler-link'} />
                  </Button>
                </div>
              )
            })}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  )
}

export default ConnectionsTab

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Typography from '@mui/material/Typography'
import Checkbox from '@mui/material/Checkbox'
import Button from '@mui/material/Button'
import Table from '@mui/material/Table'
import TableBody from '@mui/material/TableBody'
import TableCell from '@mui/material/TableCell'
import TableContainer from '@mui/material/TableContainer'
import TableHead from '@mui/material/TableHead'
import TableRow from '@mui/material/TableRow'
import Alert from '@mui/material/Alert'
import CircularProgress from '@mui/material/CircularProgress'

// Component Imports
import CustomTextField from '@core/components/mui/TextField'
import MenuItem from '@mui/material/MenuItem'

// Supabase Imports
import { createClient } from '@/utils/supabase'

// Context Imports
import { useLanguage } from '@/contexts/LanguageContext'

type NotificationType = 'new_for_you' | 'account_activity' | 'new_browser' | 'new_device'

type NotificationSettings = {
  notifications: {
    [key in NotificationType]: {
      email: boolean
      browser: boolean
      app: boolean
    }
  }
  when_to_send: 'online' | 'always' | 'never'
}

const defaultSettings: NotificationSettings = {
  notifications: {
    new_for_you: { email: true, browser: true, app: true },
    account_activity: { email: true, browser: true, app: true },
    new_browser: { email: true, browser: true, app: false },
    new_device: { email: true, browser: false, app: false }
  },
  when_to_send: 'online'
}

const NotificationsTab = () => {
  const [settings, setSettings] = useState<NotificationSettings>(defaultSettings)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null)

  const supabase = createClient()
  const { t } = useLanguage()

  const notificationLabels: Record<NotificationType, string> = {
    new_for_you: t.notifications.new_for_you,
    account_activity: t.notifications.account_activity,
    new_browser: t.notifications.new_browser,
    new_device: t.notifications.new_device
  }

  useEffect(() => {
    const fetchSettings = async () => {
      setLoading(true)
      const {
        data: { user }
      } = await supabase.auth.getUser()
      if (user) {
        const { data } = await supabase.from('user_settings').select('notifications').eq('user_id', user.id).single()

        if (data?.notifications) {
          setSettings(prev => ({ ...prev, ...data.notifications }))
        }
      }
      setLoading(false)
    }

    fetchSettings()
  }, [])

  const handleCheckboxChange = (type: NotificationType, channel: 'email' | 'browser' | 'app') => {
    setSettings(prev => ({
      ...prev,
      notifications: {
        ...prev.notifications,
        [type]: {
          ...prev.notifications[type],
          [channel]: !prev.notifications[type][channel]
        }
      }
    }))
  }

  const handleSelectChange = (value: string) => {
    setSettings(prev => ({ ...prev, when_to_send: value as any }))
  }

  const handleSave = async () => {
    setMessage(null)
    setSaving(true)
    const {
      data: { user }
    } = await supabase.auth.getUser()

    if (user) {
      const { error } = await supabase.from('user_settings').upsert({
        user_id: user.id,
        notifications: settings,
        updated_at: new Date().toISOString()
      })

      if (error) {
        setMessage({ type: 'error', text: t.notifications.error_save })
      } else {
        setMessage({ type: 'success', text: t.notifications.success_save })
      }
    }
    setSaving(false)
  }

  if (loading)
    return (
      <div className='p-4 flex justify-center'>
        <CircularProgress />
      </div>
    )

  return (
    <Card>
      <CardHeader title={t.notifications.recent_devices} subheader={t.notifications.permission_text} />
      <CardContent>
        {message && (
          <Alert severity={message.type} className='mb-4'>
            {message.text}
          </Alert>
        )}
        <TableContainer className='border rounded'>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>{t.notifications.table_type}</TableCell>
                <TableCell align='center'>{t.notifications.table_email}</TableCell>
                <TableCell align='center'>{t.notifications.table_browser}</TableCell>
                <TableCell align='center'>{t.notifications.table_app}</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {(Object.keys(notificationLabels) as NotificationType[]).map(type => (
                <TableRow key={type}>
                  <TableCell>{notificationLabels[type as NotificationType]}</TableCell>
                  <TableCell align='center'>
                    <Checkbox
                      checked={settings.notifications[type as NotificationType]?.email ?? false}
                      onChange={() => handleCheckboxChange(type as NotificationType, 'email')}
                    />
                  </TableCell>
                  <TableCell align='center'>
                    <Checkbox
                      checked={settings.notifications[type as NotificationType]?.browser ?? false}
                      onChange={() => handleCheckboxChange(type as NotificationType, 'browser')}
                    />
                  </TableCell>
                  <TableCell align='center'>
                    <Checkbox
                      checked={settings.notifications[type as NotificationType]?.app ?? false}
                      onChange={() => handleCheckboxChange(type as NotificationType, 'app')}
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </CardContent>
      <CardContent>
        <Typography className='mbe-4 font-medium'>{t.notifications.send_question}</Typography>
        <Grid container spacing={4}>
          <Grid size={{ xs: 12, sm: 6 }}>
            <CustomTextField
              select
              fullWidth
              value={settings.when_to_send}
              onChange={e => handleSelectChange(e.target.value)}
            >
              <MenuItem value='online'>{t.notifications.opt_online}</MenuItem>
              <MenuItem value='always'>{t.notifications.opt_always}</MenuItem>
              <MenuItem value='never'>{t.notifications.opt_never}</MenuItem>
            </CustomTextField>
          </Grid>
          <Grid size={{ xs: 12 }} className='flex gap-4'>
            <Button variant='contained' onClick={handleSave} disabled={saving}>
              {saving ? t.common.saving : t.common.save}
            </Button>
            <Button variant='tonal' color='secondary' onClick={() => setSettings(defaultSettings)}>
              {t.common.reset}
            </Button>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  )
}

export default NotificationsTab

'use client'

// React Imports
import { useState } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Button from '@mui/material/Button'
import Alert from '@mui/material/Alert'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Typography from '@mui/material/Typography'

// Component Imports
import CustomTextField from '@core/components/mui/TextField'

// Supabase Imports
import { createClient } from '@/utils/supabase'

// Context Imports
import { useLanguage } from '@/contexts/LanguageContext'

const SecurityTab = () => {
  // States
  const [currentPassword, setCurrentPassword] = useState('')
  const [newPassword, setNewPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [showPassword, setShowPassword] = useState(false)
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null)
  const [loading, setLoading] = useState(false)

  const supabase = createClient()
  const { t } = useLanguage()

  const handleClickShowPassword = () => setShowPassword(show => !show)

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setMessage(null)

    if (newPassword !== confirmPassword) {
      setMessage({ type: 'error', text: t.security.error_match })
      return
    }

    if (newPassword.length < 6) {
      setMessage({ type: 'error', text: t.security.error_length })
      return
    }

    setLoading(true)

    try {
      const { error } = await supabase.auth.updateUser({ password: newPassword })

      if (error) throw error

      setMessage({ type: 'success', text: t.security.success_update })
      setCurrentPassword('')
      setNewPassword('')
      setConfirmPassword('')
    } catch (error: any) {
      setMessage({ type: 'error', text: error.message || t.common.error })
    } finally {
      setLoading(false)
    }
  }

  return (
    <Grid container spacing={6}>
      <Grid size={{ xs: 12 }}>
        <Card>
          <CardHeader title={t.security.change_password} />
          <CardContent>
            <form onSubmit={handleSubmit}>
              {message && (
                <Alert severity={message.type} sx={{ mb: 4 }}>
                  {message.text}
                </Alert>
              )}
              <Grid container spacing={6}>
                <Grid size={{ xs: 12, sm: 6 }}>
                  <CustomTextField
                    fullWidth
                    label={t.security.current_password}
                    type={showPassword ? 'text' : 'password'}
                    value={currentPassword}
                    onChange={e => setCurrentPassword(e.target.value)}
                    slotProps={{
                      input: {
                        endAdornment: (
                          <InputAdornment position='end'>
                            <IconButton
                              edge='end'
                              onClick={handleClickShowPassword}
                              onMouseDown={e => e.preventDefault()}
                            >
                              <i className={showPassword ? 'tabler-eye-off' : 'tabler-eye'} />
                            </IconButton>
                          </InputAdornment>
                        )
                      }
                    }}
                  />
                </Grid>
              </Grid>
              <Grid container spacing={6} className='mbe-4 mt-6'>
                <Grid size={{ xs: 12, sm: 6 }}>
                  <CustomTextField
                    fullWidth
                    label={t.security.new_password}
                    type={showPassword ? 'text' : 'password'}
                    value={newPassword}
                    onChange={e => setNewPassword(e.target.value)}
                    helperText={t.security.password_helper}
                    slotProps={{
                      input: {
                        endAdornment: (
                          <InputAdornment position='end'>
                            <IconButton
                              edge='end'
                              onClick={handleClickShowPassword}
                              onMouseDown={e => e.preventDefault()}
                            >
                              <i className={showPassword ? 'tabler-eye-off' : 'tabler-eye'} />
                            </IconButton>
                          </InputAdornment>
                        )
                      }
                    }}
                  />
                </Grid>
                <Grid size={{ xs: 12, sm: 6 }}>
                  <CustomTextField
                    fullWidth
                    label={t.security.confirm_password}
                    type={showPassword ? 'text' : 'password'}
                    value={confirmPassword}
                    onChange={e => setConfirmPassword(e.target.value)}
                    slotProps={{
                      input: {
                        endAdornment: (
                          <InputAdornment position='end'>
                            <IconButton
                              edge='end'
                              onClick={handleClickShowPassword}
                              onMouseDown={e => e.preventDefault()}
                            >
                              <i className={showPassword ? 'tabler-eye-off' : 'tabler-eye'} />
                            </IconButton>
                          </InputAdornment>
                        )
                      }
                    }}
                  />
                </Grid>
                <Grid size={{ xs: 12 }}>
                  <Typography className='mbe-2 font-medium'>{t.security.requirements_title}</Typography>
                  <ul className='pl-4 list-disc space-y-1 text-textSecondary'>
                    <li>{t.security.req_1}</li>
                    <li>{t.security.req_2}</li>
                    <li>{t.security.req_3}</li>
                  </ul>
                </Grid>
                <Grid size={{ xs: 12 }} className='flex gap-4'>
                  <Button variant='contained' type='submit' disabled={loading}>
                    {t.common.save}
                  </Button>
                  <Button
                    variant='tonal'
                    color='secondary'
                    onClick={() => {
                      setCurrentPassword('')
                      setNewPassword('')
                      setConfirmPassword('')
                    }}
                  >
                    {t.common.reset}
                  </Button>
                </Grid>
              </Grid>
            </form>
          </CardContent>
        </Card>
      </Grid>
      <Grid size={{ xs: 12 }}>
        <Card>
          <CardHeader title={t.security.two_factor_title} />
          <CardContent>
            <Typography className='mbe-4 font-medium'>{t.security.two_factor_text}</Typography>
            <Typography className='mbe-6'>{t.security.two_factor_sub}</Typography>
            <Button variant='contained' onClick={() => setMessage({ type: 'success', text: t.security.feature_soon })}>
              {t.security.two_factor_btn}
            </Button>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  )
}

export default SecurityTab
